import { Link, useNavigate } from 'react-router-dom'
import Heading from './Heading'
import MyNav from './NavBar'
import resultSet from '../data/cars.json'

const ReadCRUD = () => {
	const navigate = useNavigate() //Declare for route

	/*  1. Set the item value to sessionStorage */
	const setStorage = (id, brand, model, price) => {
		sessionStorage.setItem('id', id)
		sessionStorage.setItem('brand', brand)
		sessionStorage.setItem('model', model)
		sessionStorage.setItem('price', price)
	}

	/* 2. Delete (CRUD) the item */
	function deleteData(id) {
		//2.1. Get index of item
		var index = resultSet.map(function (e) {
			return e.id
		}).indexOf(id)

		// 2.2. Deleting the item by index
		resultSet.splice(index, 1)

		// 2.3. Redirect to Home page
		navigate('/')
	}
	/* 3. Read (CRUD) all data in list  */
	return (	
		<>
			<div className='location-1-1'>
				<Heading /> {/* Heading: Logo, Title */}
			</div>
			<div className='location-1-2'></div>
			<div className='location-2-1'>
				<MyNav />	{/* MyName: Right Vertical Navbar */}
			</div>
			<div className='location-2-2'>
				<table className='table table-bordered'> {/* Main workspace */}
				{/* 3.1. Column Name */}
					<tr>
						<th>Brand</th>
						<th>Model</th>
						<th>Price</th>
						<th>Actions</th>
					</tr>
				{/* 3.2 All records of Table */}
					{resultSet.length > 0 ? ( /*[syntax] length > 0 ? (result):(error) */	
						//3.2.1. result
						resultSet.map(rs => (
							<tr>
								{/* 3.2.1.1. The record of result */}
								<td>{rs.brand}</td>
								<td>{rs.model}</td>
								<td>{rs.price}</td>
								<td>
								{/* 3.2.1.2. Action Buttons */}
								<Link to='/edit'>
										<button  className='btn btn-link'
											onClick={() =>
											setStorage(rs.id, rs.brand, rs.model, rs.price)}
										>
											Update</button>
									</Link>
									|
								<Link>
									<button  className='btn btn-link'
										onClick={() => {
											if (window.confirm("Delete the item?")) {
												deleteData(rs.id)
											}
										}}>Delete</button>
								</Link>
								</td>
							</tr>
						))
					) : ( 
						//3.2.2. Error
						<tr>
							<td colSpan={4}>No any car!</td>
						</tr>
					)}
				</table>
			</div>
		</>
	)
}

export default ReadCRUD
