import { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import Heading from './Heading'
import MyNav from './NavBar'
import resultSet from '../data/cars.json'

const UpdateCRUD = () => {
	const navigate = useNavigate()	//Declare for page redirect
	
	const [id, setID] = useState('')
	const [brand, setBrand] = useState('')
	const [model, setModel] = useState('')
	const [price, setPrice] = useState('')

	

	// Getting an index of an entry with an id


	// Function for handling the edit and
	// pushing changes of editing/updating
	const handelSubmit = (e) => {
		var index = resultSet.map(function (e) {
			return e.id
		}).indexOf(id)
		// Preventing from reload
		e.preventDefault()
		const isOK = window.confirm('Are you sure to Update the item?')
		if (isOK) {
			let a = resultSet[index]

			// Putting the value from the input
			// textfield and replacing it from
			// existing for updation
			a.brand = brand
			a.model = model
			a.price = price

			// Redirecting to main page
			navigate('/')

		}
		// Getting an index of an resultSet

	}


	// Useeffect take care that page will
	// be rendered only once
	useEffect(() => {
		setID(sessionStorage.getItem('id'))
		setBrand(sessionStorage.getItem('brand'))
		setModel(sessionStorage.getItem('model'))
		setPrice(sessionStorage.getItem('price'))

	}, [])

	return (
		<>
			<div className='location-1-1'>
				<Heading />
			</div>
			<div className='location-1-2'></div>
			<div className='location-2-1'>
				<MyNav />
			</div>
			<div className='location-2-2'>

				<form
				//   onSubmit={event => {
				//     event.preventDefault()
				//     const isOK = window.confirm("Are you sure to Update the item?")
				//     if (isOK) {
				//       if (!car.brand || !car.model || !car.price)
				//         return
				//         props.updateCar(car.id, car)
				//     }
				//   }}
				>
					<div className='myForm'>
						<table>
							<tr>
								<td align="right">Brand:</td>
								<td>
									<input value={brand} onChange={e => setBrand(e.target.value)} />
								</td>
							</tr>
							<tr>
								<td align="right">Model:</td>
								<td>
									<input value={model} onChange={e => setModel(e.target.value)} />
								</td>
							</tr>
							<tr>
								<td align="right">Price:</td>
								<td>
									<input value={price} onChange={e => setPrice(e.target.value)} />
								</td>
							</tr>
							<tr>
								<td></td>
								<td>
									<button
										className='btn btn-outline-primary'
										onClick={e => handelSubmit(e)}
										variant="primary" type="submit" size="lg">
										Update
									</button>
								</td>
							</tr>
						</table>
					</div>
				</form>
			</div>
		</>
	)
}

export default UpdateCRUD
